import { expect } from "chai";
import { link } from "../src/link";

const libraryAddress = "0xA193E42526F1FEA8C99AF609dcEabf30C1c29fAA";

describe("UNIT: Linking", () => {
  describe("link with Solidity4 bytecode", () => {
    // eslint-disable-next-line max-len
    const compiledBytecode =
      "__test/projects/custom_solidity_4/cust__";
    // eslint-disable-next-line max-len
    const runtimeBytecode =
      "";

    it("long name", async () => {
      const libraryName =
        "test/projects/custom_solidity_4/custom_contracts/MyLibrary.sol:MyLibrary";
      const contract = { evm: { bytecode: { object: compiledBytecode } } };
      link(contract, libraryName, libraryAddress);
      expect(contract.evm.bytecode.object).to.equal(runtimeBytecode);
    });

    it("invalid name", async () => {
      const libraryName = "non_existing";
      const contract = { evm: { bytecode: { object: compiledBytecode } } };
      expect(() => {
        link(contract, libraryName, libraryAddress);
      }).to.throw(Error, "Can't link 'non_existing'.");
    });
  });

  describe("link with Solidity5 bytecode", () => {
    // eslint-disable-next-line max-len
    const compiledBytecode =
      "__$834a779d64fff2a3ff626ee032010a25b6$__";
    // eslint-disable-next-line max-len
    const runtimeBytecode =
      "";

    it("proper name", async () => {
      const libraryName =
        "test/projects/custom/custom_contracts/MyLibrary.sol:MyLibrary";
      const contract = { evm: { bytecode: { object: compiledBytecode } } };
      link(contract, libraryName, libraryAddress);
      expect(contract.evm.bytecode.object).to.equal(runtimeBytecode);
    });

    it("invalid name", async () => {
      const libraryName = "non_existing";
      const contract = { evm: { bytecode: { object: compiledBytecode } } };
      expect(() => {
        link(contract, libraryName, libraryAddress);
      }).to.throw(Error, "Can't link 'non_existing'.");
    });
  });
});
